select fileid,filename from #{db_schema}.#{db_table_dpcn_logfile}
where 
	ms_errore like '%E004%'
	and ts_download is null
limit 1;

update #{db_schema}.#{db_table_dpcn_logfile}
set
	ts_errore = null,
	ms_errore = null
where fileid = ${fileid.0} and ms_errore like '%DBALLE' or ms_errore like '%AMQP';

