entry.value:
update public.dpcn_logfile
set ts_download = to_timestamp(${now():toNumber()}/1000)
where fileid = ${fileid.0};

delete from public.dpcn_dati dati where fileid in (
	select fileid from public.dpcn_logfile
	where ts_delete is not null
);

update public.dpcn_logfile
set ts_download = to_timestamp(${now():toNumber()}/1000)
where fileid = ${fileid.0};

update public.dpcn_logfile
set ts_ingestion = null
where fileid = ${fileid.0};

update public.dpcn_logfile
set ts_ingestion = to_timestamp(${now():toNumber()}/1000)
where fileid = ${fileid.0};

select distinct 'dpcn-' || lower(replace(replace(replace(regione,' ',''),'/',''),'-','')) as network
from public.dpcn_anagrafica ana
where lower(replace(replace(replace(regione,' ',''),'/',''),'-','')) <> 'emiliaromagna';
